package arrays

import (
	"fmt"
	"reflect"
	"testing"
)

func TestNextPermutation(t *testing.T) {
	for _, test := range nextPermutationTestData {
		fmt.Println("Values in test.p before applying next permutation:", test.p)
		if got := NextPermutation(test.p); !reflect.DeepEqual(got, test.want) {
			t.Errorf("NextPermutation() = %v, want %v", got, test.want)
		}
	}
}

